function locationtoggle() {
    if ($(window).width() <= 767) {
        $(".fp-slides").css("height", "50%");
        $("ul.list-location li.title").click(function () {
            if ($(this).hasClass("up")) {
                $(".fp-slides").css("height", "100%");
            }
            if ($(this).hasClass("down")) {

                $(".fp-slides").css("height", "50%");
            }
        })
    }
}

function changeBanner() {
    if ($(window).width() < 768) {
        if ($(".page-banner").data("bgmobile")) {
            $(".page-banner").css("background-image", "url('" + $(".page-banner").data("bgmobile") + "')");
        }
    } else {
        if ($(".page-banner").data("bg")) {
            $(".page-banner").css("background-image", "url('" + $(".page-banner").data("bg") + "')");
        }
    }

}
